INSERT INTO `doctors_info` VALUES ('dnnoigignkd85dnn', '杜元平', '|胸膜炎|', '|xiongmoyan|', '副主任医师 副教授', '南充市中心医院', '感染病科', '3.6', '中西医结合治疗各种肝病、各型肺结核、肾综合征出血热、脑膜炎及其它感染性疾病', '杜元平，男，副主任医师，副教授，毕业于成都中医药大学，多次在省内外接受专业培训。从事中西医结合、感染病科临床、科研及教学工作近20年，擅长中西医结合治疗各种肝病、各型肺结核、肾综合征出血热、脑膜炎及其它感染性疾病。在省级刊物上发表论文5篇。', '1', 'http://www.haodf.com/doctor/DE4r0BCkuHzduTmZIlHeCoEzBhPT2.htm', '2016-05-06 18:16:06');
