INSERT INTO `doctors_info` VALUES ('gingiuejuiuee8iu', '顾毅', '|甲状腺结节|', '|jiazhuangxianjiejie|', '副主任医师', '四川省人民医院', '血管甲状腺专科', '3.1', '外周血管疾病、甲状腺疾病的诊治', '顾毅，男，副主任医师，重庆医科大学硕士，美国哈佛大学麻省总医院博士后，美国宾西法尼亚大学访问学者。从事普外科工作10多年，擅长外周血管疾病、甲状腺疾病的诊治。', '1', 'http://www.haodf.com/doctor/DE4r0BCkuHzdeGIJPDonLBKpXQJ1u.htm', '2016-05-06 18:14:10');
INSERT INTO `doctors_info` VALUES ('ginssnipe909uagh', '辜格嘉', '|梅毒|', '|meidu|', '主治医师', '成都市第十人民医院', '皮肤科', '4', '皮肤病性病、男性病、感染性疾病的诊治', '辜格嘉，男，外科皮肤性病主治医师，1992年毕业于苏州医学院临床医学系，2004年曾在四川省皮肤病性病研究所进修皮肤病性病及男性病一年，2005年在北京参加艾滋病医师提高班学习。擅长皮肤病性病、男性病、感染性疾病的诊治。', '1', 'http://www.haodf.com/doctor/DE4r0BCkuHzduwnjPvnWKEMSMwtPM.htm', '2016-05-06 18:14:27');
