INSERT INTO `doctors_info` VALUES ('lnazyngib9lnazyn', '罗骏', '|附睾炎|', '|fugaoyan|', '主任医师', '乐山市中医院', '中医内科', '3.6', '诊治前列腺疾病、性传播疾病、男性功能障碍、男性不育等泌尿生殖系统疾病及急慢性湿疹、荨麻疹、痤疮、神经性皮炎、银屑病等皮肤疾病', '罗骏，男，主任中医师，毕业于成都中医药大学，现任四川省中医男科专委会委员、四川省中西医结合学会男科专委会委员，从事泌尿、男科、皮肤科临床及科研工作20余年，具有丰富的临床经验和较高的学术水平，在国家级杂志发表学术论文多篇。擅长于诊治前列腺疾病、性传播疾病、男性功能障碍、男性不育等泌尿生殖系统疾病及急慢性湿疹、荨麻疹、痤疮、神经性皮炎、银屑病等皮肤疾病。', '0', 'http://www.haodf.com/doctor/DE4r0BCkuHzduTXKUEnWKEMSMwtPM.htm', '2016-05-06 18:12:57');
